css:
@font-face {
    font-family: 'Montserrat';
    src: url("/files/Montserrat.ttf");
    font-style: normal;
    font-weight: normal;
}


#main_table_container{
    margin-top: 2px;
    position: relative;
    font-family: 'Montserrat';
}

#address_invoices{
    display: grid;
    grid-template-columns: 50% 50%;
    column-gap: 2em;
}
#invoice {
    position: relative;
    top: -20px;
}
#logo_container{
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-bottom: 30px;
}
#logo{
    width: 220px;
    height: 80px;
    position: relative;
    right: 30px;
    top: -2px;
    float: right;
}
#date_table tr td {
    border: none;
    line-height: 5px;
    font-size: 0.8em;

}
#date_table tbody tr {
    border: none;
}
#data_table tr td {
    font-weight: 500;
    color: #000000;
}
#invoice {
    position: relative;
    top: -50px;
}
#invoice_header{
    position: relative;
    font-weight: bold;
    font-size: 2em;
    letter-spacing: 3px;
}
#billing {
    position: relative;
    top: -180px;

}
#bank_details {
    position: relative;
    top: -10px;
}

#bank_details p {
    font-size: 0.8em;
    padding: 0;
    margin-top: 0px;
    margin-bottom: 0px;
}
#item_table {
    width: 100%;

}
#item_table thead tr th{
    color: #000000;
    font-weight: bold;
    font-size: 10px;
}
#item_table thead{
    border-bottom: 3px solid #1e8b08;
}
#item_table tr td {
    line-height: 10px;
    font-size: 10px;
    color: #000000;
}
#item_table .center{
    text-align:center;
}
#total_table thead {
     border-bottom: 1px solid #1e8b08;

}
#total_table tr td{
    font-size: 13px;
}

footer {
    position: absolute;
    bottom: 0;
    width: 100%;
    margin: 0;

}
#footer_content{
    display: flex;
    font-family: 'Montserrat';
}
#footer_content p {
    font-size: 0.8em;
}

#bottom{
    border-top: 3px solid #f5a904;
    text-align: center

}
#bottom p {
    font-size: 0.8em;
    font-weight: bold;
    font-family: 'Montserrat';
}
